mapper.select:
select

from PMS_MERCHANT_INFO

	 and MOBILEPHONE = #{mobilephone,jdbcType=VARCHAR}
	 and PASSWORD = #{password,jdbcType=VARCHAR}
	 and MERC_ID = #{mercId,jdbcType=VARCHAR}
	 and CREATION_NAME = #{creationName,jdbcType=VARCHAR}

	 and CUSTOMERTYPE = #{customertype,jdbcType=VARCHAR}
	 and CRP_ID_NO = #{crpIdNo,jdbcType=VARCHAR}
	 and EMAIL= #{email,jdbcType=VARCHAR}
	 and EXTERNALID= #{externalId,jdbcType=VARCHAR}
	 and OAGENTNO= #{oAgentNo,jdbcType=VARCHAR}
	 and MERC_STS = #{mercSts,jdbcType=VARCHAR}

select arc.RATE SETTLERATE,arc.BOTTOM_POUNDAGE MINSETTLEPOUNDAGE
from PMS_MERCHANT_INFO pmi ,APP_RATE_CONFIG arc
where arc.OAGENTNO=pmi.OAGENTNO and arc.RATE_TYPE=pmi.SETTLERATETYPE and pmi.MERC_ID= #{mercid,jdbcType=VARCHAR}
